import axios from 'axios';
import React, { useEffect, useState } from 'react'

function Add_Services() {
    const [formvalue, setFormvalue] = useState({
        id: "",
        name: "",
        desc: "",
        price: "",
    });
    const getform = (e) => {
        setFormvalue({ ...formvalue, id: new Date().getTime().toString(), [e.target.name]: e.target.value });
        console.log(formvalue);
    }

    const submithandel = async (e) => {
        e.preventDefault(); // stop page reload 
        const res = await axios.post(`http://localhost:3000/categories`, formvalue);
        //console.log(res);
        if (res.status == 201) {
            setFormvalue({ ...formvalue, Name: "", Image: "" });
            alert('Categories submited success');
            return false;
        }
    }

    return (
        <>
               <div className="content-wrapper mt-5 ms-5">
            <div className="container-fluid ">
                <div className="row pad-botm">
                    <div className="col-md-12">
                        <h4 className="header-line">Add Services</h4>
                    </div>
                </div>
                <div className="row">
                    <div className="col-md-12 col-sm-12 col-xs-12">
                        <div className="panel panel-info">

                            <div className="panel-body">
                                <form role="form" method='post' onSubmit={submithandel}> 
                                    
                                    <div className="form-group">
                                        <label>Item Name</label>
                                        <input className="form-control" type="text"value={formvalue.name} onChange={getform} />
                                    </div>
                                    <div className="form-group">
                                        <label>Item Description</label>
                                        <input className="form-control" type="text"value={formvalue.Name} onChange={getform} />
                                    </div>
                                    <div className="form-group">
                                        <label> Item Price</label>
                                        <input className="form-control" type="text" />
                                    </div>
                                </form></div>
                            <br />
                            <button type="submit" className="btn btn-info">Add Services</button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
        </>




    )
}

export default Add_Services
