import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { NavLink } from 'react-router-dom';
import { toast } from 'react-toastify';

function Manage_Employee() {
    const [data, setData] = useState([]);
    useEffect(() => {
        fetch()
    }, [])

    const fetch = async () => {
        const res = await axios.get('http://localhost:3000/employee')
        console.log(res.data);
        setData(res.data)
    }

    const Handeldelete = async (id) => {
        const res = await axios.delete(`http://localhost:3000/employee/${id}`);
        fetch();
    }
    const [formvalue, setFormvalue] = useState({
        id: "",
        name: "",
        email: "",
        mobile: "",
        password: "",
    });
    const editdata = async (id) => {
        const res = await axios.get(`http://localhost:3000/user/${id}`);
        console.log(res.data);
        setFormvalue(res.data);
    }

    const getform = (e) => {
        setFormvalue({ ...formvalue, [e.target.name]: e.target.value });
        console.log(formvalue);
    }

    const validation = () => {

        var result = true;
        if (formvalue.name == "") {
            toast.error('Name Field is required');
            result = false;
            return false;
        }
        if (formvalue.email == "") {
            toast.error('Email Field is required');
            result = false;
            return false;
        }

        if (formvalue.mobile == "") {
            toast.error('Mobile Field is required');
            result = false;
            return false;
        }
        if (formvalue.img == "") {
            toast.error('Image Field is required');
            result = false;
            return false;
        }
        return result;

    }

    const submithandel = async (e) => {
        e.preventDefault(); // stop page reload
        if (validation()) {
            const res = await axios.patch(`http://localhost:3000/user/${formvalue.id}`, formvalue);
            console.log(res);
            if (res.status == 200) {
                setFormvalue({ ...formvalue, name: "", email: "", mobile: "", img: "" });
                toast.success('Update success');
                fetch();
            }
        }
    }


    return (
        <>
            <div className="container mt-5">
                <h2>Manage Employees</h2>
                <table className="table table-striped">
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Name</th>
                            <th>Email</th>
                            <th>Mobile</th>
                            <th>Password</th>
                            <th>Action</th>
                            <th>Status</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            data.map((value) => {
                                return (
                                    <tr>
                                        <td>
                                            <tr>{value.id}</tr>
                                        </td>
                                        <td>
                                            <tr>{value.Name}</tr>
                                        </td>
                                        <td>
                                            <tr>{value.email}</tr>
                                        </td>
                                        <td>
                                            <tr>{value.mobile}</tr>
                                        </td>
                                        <td>
                                            <tr>{value.password}</tr>
                                        </td>
                                        <td>
                                            <a href=""><button className="btn btn-sm btn-primary">Edit</button></a>
                                            <button className="btn btn-sm btn-danger" onClick={() => Handeldelete(value.id)}>Delete</button>
                                        </td>
                                        <td>
                                            <tr>{value.status}</tr>
                                        </td>
                                    </tr>
                                )
                            })
                        }
                        {/* Add more rows for additional doctors as needed */}
                    </tbody>
                </table>
            </div>
            <div>

            
            </div>
        </>

    )
}

export default Manage_Employee
