import React, { useState } from 'react'
import axios from 'axios';

const [formvalue, setFormvalue] = useState({
  id: "",
  name: "",
  email: "",
  password: "",
});

const getform = (e) => {
  setFormvalue({ ...formvalue, id: new Date().getTime().toString(), [e.target.name]: e.target.value });
  console.log(formvalue);
}

const submithandel = async (e) => {
  e.preventDefault(); // stop page reload 
  const res = await axios.post(`http://localhost:3000/employee`, formvalue);
  //console.log(res);
  if (res.status == 201) {
    setFormvalue({ ...formvalue, name: "", email: "", password: "" });
    alert('Employee Added success');
    return false;
  }
}

function Add_Employee() {
  return (
    <>
      <div className="container mt-5">
        <h2>Add Employee</h2>
        <form>
          <div className="form-group">
            <label htmlFor="name">Name:</label>
            <input type="text" className="form-control" id="name" placeholder="Enter name" nam  required />
          </div>
          <div className="form-group">
            <label htmlFor="specialty">Email</label>
            <input
              type="text"
              className="form-control"
              id="specialty"
              placeholder="Enter Email"
              required
            />
            <div className="form-group">
              <label htmlFor="contact">Mobile</label>
              <input
                type="tel"
                className="form-control"
                id="contact"
                placeholder="Enter Mobile number"
                required
              />
            </div>
          </div>
          <div className="form-group">
            <label htmlFor="hospital">Password</label>
            <input
              type="text"
              className="form-control"
              id="hospital"
              placeholder="Enter password"
              required
            />
          </div>


          <br />
          <button type="submit" className="btn btn-primary">
            Submit
          </button>
        </form>
      </div>
    </>


  )
}

export default Add_Employee
